import React from 'react';

import { useParams } from "react-router-dom";
import { Header } from '../components/Header';
import { DB } from './scripts/ApiDB';


const ReplyView = (props) => { 

	const { link } = useParams();

	return <ReplyComponent link={link}/>
}


class ReplyComponent extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			replies: []
		}

		DB.GetReply(this.props.link).then(result => {
			console.log(result)
			this.setState({ replies: result })
		});
	}

	render() {
		let questions = [];
    	let answers = [];
		if (this.state.replies && this.state.replies.questions) {
			questions = this.state.replies.questions.split('|');
    		answers = this.state.replies.answers.split('|');
		}
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						questions.map((e, i) => {
							return <article class="question-form" key={++i}>
								<section>
									<div>Вопрос: {e}</div>
								</section>

								<section>
									<div>Ответ: {answers[i]}</div>
								</section>
							</article>
						}) || <></>
					}
					</section>
				</article>
			</main>
		</>
	}
}
export { ReplyView };
