import React from 'react';
import { ReplyDB } from '../hooks/ReplyDB';


class ReplyList extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			replies: []
		}

		ReplyDB.GetReplyList(this.props.link).then(result => {
			this.setState({ replies: result })
		});
	}

	render() {
		let replies = [];
		if (this.state.replies)
			replies = this.state.replies;
		return <section id="replyPollView">
			{
				replies.map((row, i) => {
					return <button className='reply-btn-link' key={i++} onClick={() => {
						this.props.ShowReplyView(row['link']);
					}}>{i + 1}. Ответ от {row.creationTime.replace(/T[\s\S]+/g, '')}
					</button>
				}) || <></>
			}
		</section>
	}
}
export { ReplyList };
