import React from 'react';
import '../styles/fonts.css';
import '../styles/layout.css';
import '../styles/header.css';
import '../styles/sidebar.css';

import './styles/views.css';
import './styles/add-poll.css';
import './styles/list-poll.css';

import { Header } from '../components/Header';
import { Greetings } from '../components/Greetings';
import { AddPoll } from '../components/AddPoll';
import { Profile } from '../components/Profile';
import { AsideButton } from './AsideButton';


class Polls extends React.Component {

    constructor(props) {
        super(props);

		this.state = {
			content : 0
		}
    }

	GetView(content) {
		switch(content) {
			case(0): return <Greetings />
			default: return <div>no</div> 
		}
	}

	render() {
		return <>
			<Header />
			<aside>
				<AsideButton viewName='addPoll' viewImage='addPollAside' buttonSign='New Poll'/>
				<AsideButton viewName='listPoll' viewImage='listPollAside' buttonSign='My Polls'/>
			</aside>
			<main>
				<article id="views">

					<Greetings />
					<Profile />
					<AddPoll />

					<section id="listPollView"></section>
				</article>
			</main>
		</>
	}
}
export default Polls;
