import React from 'react';
import { DB } from './scripts/ApiDB';


class ReplyView extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			replies: []
		}

		DB.GetReply(this.props.link).then(result => {
			this.setState({ replies: result })
		});
	}

	render() {
		let questions = [];
    	let answers = [];
		if (this.state.replies && this.state.replies.questions) {
			questions = this.state.replies.questions.split('|');
    		answers = this.state.replies.answers.split('|');
		}
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						questions.map((e, i) => {
							return <article className="question-form" key={++i}>
								<section>
									<div>Вопрос: {e}</div>
								</section>

								<section>
									<div>Ответ: {answers[i]}</div>
								</section>
							</article>
						}) || <></>
					}
					</section>
				</article>
			</main>
		</>
	}
}
export { ReplyView };
