import React from 'react';
import { ReactComponent as  ProfileIcon } from '../styles/icons/user'
import { ReactComponent as  LogoutIcon } from '../styles/icons/logout'


class Header extends React.Component {

    render() {
        return <header>
            <h1 className="header-logo non-select" data-name="start"> 
                <a href="/polls/">MIREA</a>
                <span style={{marginLeft: '-3px'}}>.polls</span>
            </h1>
            <section>
                <ProfileIcon id="profileBtn" className="profile-btn" data-name="profile" onClick={() => {
                    this.props.ShowProfile();
                }} />
                <LogoutIcon id="logoutBtn" className="logout-btn" data-name="logout" onClick={() => {
                    delete localStorage['token'];
                    window.location.href = '/';
                }} />
            </section>
        </header>
    }

}

export { Header }
