import React from 'react';
import './styles/auth-button.css';
import { DB } from './scripts/AuthDB';
import { LocalDB } from './scripts/AuthLocal';
import { Link } from 'react-router-dom';

class AuthButton extends React.Component {

	render() {
		return <section className='authorization-confirm' onClick={async () => {
            let user = {
                login: document.getElementById('username').value,
                password: document.getElementById('password').value,
                email: document.getElementById('email').value,
                phone: document.getElementById('phone').value
            }
    
            let responseData;
    
            if (this.props.showRegFields)
                responseData = await DB.Registry(user);
            else
                responseData = await DB.Login(user);

            console.log(responseData);
    
            if (responseData != null) {
                LocalDB.SetToken(responseData['token']);
                
                if (responseData['role'] == 'ROLE_USER')
                    window.location.replace('/mypolls/')
            }
        }}>
            <div>123123</div>
            <Link to='/polls/' onClick={() => {
                setTimeout(() => {
                    
                }, 1000);
            }} >{this.props.showRegFields ? 'CREATE ACCOUNT' : 'CONTINUE'}</Link>
        </section>
	}
}
export { AuthButton };
