import React from 'react';
import '../styles/add-poll.css';

class AddPoll extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			bebra: []
		}
	}

	render() {
		return <section id="addPollView">
			<input className="poll-name-input" placeholder="Название опроса" />
            <input className="poll-date-input"  placeholder="Дата окончания" />
            <div className="addPoll-buttons">
                <button id="addQuestionBtn" onClick={() => {
					this.state.bebra.push(1);
					this.setState({ bebra: this.state.bebra });
				}}>Добавить вопрос</button>
                <button id="postPollBtn" onClick={() => {
					let questionsStr = '';
					let answersStr = '';

					let questionForms = document.getElementsByClassName('question-form');

					for (let elem in questionForms) {

					}

					$jsf(`.question-form`).eachJSF($e => {
						$inputs = $e.find('input');
						questionsStr += $inputs.get(0).value + '|'
						answersStr += $inputs.get(1).value + '|'
					})
					questionsStr = questionsStr.replace(/(\|)$/g, '');
					answersStr = answersStr.replace(/(\|)$/g, '');



					let pollObj = {
						name: $jsf(`.poll-name-input`).value(),
						expirationTime: $jsf(`.poll-date-input`).value(),
						answers: answersStr,
						questions: questionsStr
					};


					DB.PostPoll(pollObj).then((result) => {
						$jsf(`.question-form`).ohtml('');

						navigator.clipboard.writeText(result.link)
						alert('Ссылка на опрос скопирована!');
					});
				}}>Опубликовать</button>
            </div>

			{ this.state.bebra.map(e => 
				<article class="question-form">
					<section>
						<div>Вопрос: </div>
						<input />
					</section>

					<section>
						<div>Правильный ответ: </div>
						<input />
					</section>
            	</article>
			) }
		</section>
	}
}
export { AddPoll };
