import React from 'react';
import '../styles/fonts.css';
import '../styles/layout.css';
import '../styles/header.css';
import '../styles/sidebar.css';
import '../styles/profile.css';

import './styles/views.css';
import './styles/add-poll.css';
import './styles/list-poll.css';

import { AuthField } from '../authorize/AutField';
import { Header } from '../components/Header';
import { AsideButton } from './AsideButton';
import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';


class Polls extends React.Component {

	render() { 
		return <>
			<Header />
			<aside>
				<AsideButton viewName='addPoll' viewImage='addPollAside' buttonSign='New Poll'/>
				<AsideButton viewName='listPoll' viewImage='listPollAside' buttonSign='My Polls'/>
			</aside>
			<main>
				<article id="views">
					<section id="startView" className="active">
						<h2>Welcome to</h2>
						<h1>Mirea Polls</h1>
					</section>

					<section id="profileView" className="">
						<article className="profile-card">
							<section className="profile-type">
								<div className="non-select login active">USER</div>
							</section>
							<section className="profile-info">
								<AuthField name="login"/>
								<AuthField name="email"/>
								<AuthField name="phone"/>
								<AuthField name="password"/>
							</section>
							<section className="profile-confirm">
								<button id="profileConfirm">Confirm</button>
								<button id="profileLogout">Log out</button>
							</section>
						</article>
					</section>

					<section id="addPollView"></section>
					<section id="listPollView"></section>
				</article>
			</main>
		</>
	}
}
export default Polls;
