import React from 'react';
import './styless/fonts.css';
import './styless/layout.css';
import './styles/style.css';
import './styles/beauty_input.css';


class Authorize extends React.Component {

	constructor(props) {
		super(props);
		this.state = { showRegFields: false };
	}

	componentDidMount() {

	}


	render() { 
		return <main>
	  		<article className="authorization-card">
				<section className="authorization-type">
					<div className="active non-select login" data-text="CONTINUE" onClick={() => this.setState({showRegFields: false})}>Login</div>
					<div className="non-select signup" data-text="CREATE ACCOUNT" onClick={() => this.setState({showRegFields: true})}>Sign up</div>
				</section>
				<section className="authorization-info sign-in">
					<div>
						<input id="username" type="text" placeholder="username" required/>
						<span>username</span>
					</div>

						{ this.state.showRegFields ? (
							<div className="signup-fields">
								<input id="email" placeholder="email" required/>
								<span>email</span>
							</div>
						) : ''
						}
						{ this.state.showRegFields ? (
							<div className="signup-fields">
								<input id="phone" placeholder="phone" required/>
								<span>phone</span>
							</div>
						) : ''
						}

					<div>
						<input id="password" type="password" placeholder="password" required/>
						<span>password</span>
					</div>
				</section>
				<section className="authorization-confirm">
					<button onClick={async () => {
						console.log('sdf')
						return;
						 let user = {
							login: $nameInput.value(),
							password: $passInput.value(),
							email: $emailInput.value(),
							phone: $phoneInput.value()
						}

						let responseData;
						log(responseData)

						if ($btn.text() == "CREATE ACCOUNT")
							responseData = await DB.Registry(user);
						else
							responseData = await DB.Login(user);


						if (responseData != null) {
							LocalDB.SetToken(responseData['token']);

							if (responseData['role'] == 'ROLE_USER')
								window.location.replace('/mypolls/')
						}
					}}>CONTINUE</button>
				</section>
			</article>
  		</main>
	}
}
export default Authorize;
