import React from 'react';
import { useParams } from 'react-router-dom';
import { ReplyDB } from '../hooks/ReplyDB';


function ReplyByLink() {
    const { link } = useParams();

    return <ReplyComponent link={link} />
  }


class ReplyComponent extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			poll: []
		}

		ReplyDB.GetPoll(this.props.link).then(result => {
			this.setState({ poll: result })
		});
	}

	render() {
		let questions = '';
		if (this.state.poll && this.state.poll.questions)
			questions = this.state.poll.questions;
		return <>
            <Header ShowGreetings={() => { this.SetView('Greetings') }} ShowProfile={() => { this.SetView('Profile') }}/>
			<aside>
				<AsideButton viewName='addPoll' viewImage='addPollAside' buttonSign='New Poll' Action={() => { this.SetView('AddPoll') }}/>
				<AsideButton viewName='listPoll' viewImage='listPollAside' buttonSign='My Polls' Action={() => { this.SetView('ListPoll') }}/>
			</aside>
			<main>
				<article id="views">{this.GetView()}</article>
			</main>
        </>
	}
}
export { ReplyByLink };
