import React from 'react';

import { useParams } from "react-router-dom";
import { Header } from '../components/Header';
import { DB } from './scripts/ApiDB';


const ReplyList = (props) => { 

	const { link } = useParams();

	return <ReplyComponent link={link}/>
}


class ReplyComponent extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			replies: []
		}

		DB.GetReplyList(this.props.link).then(result => {
			this.setState({ replies: result })
		});
	}

	render() {
		let replies = [];
		if (this.state.replies)
			replies = this.state.replies;
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						replies.map((row, i) => {
							return <a key={i++} target="_blank" rel="noopener noreferrer"  href={`http://localhost:3000/replyView/${row.link}`}>{i + 1}. Ответ от {row.creationTime.replace(/T[\s\S]+/g, '')}</a>
						}) || <></>
					}
					</section>
				</article>
			</main>
		</>
	}
}
export { ReplyList };
