import React from 'react';
import './styless/fonts.css';
import './styless/layout.css';
import './styles/style.css';
import './styles/beauty_input.css';
import { $jsf } from './scripts/JSF';
import { LocalDB } from './scripts/LocalDB';
import { DB } from './scripts/DB';



class Authorize extends React.Component {

	constructor(props) {
		super(props);
		this.state = { showRegFields: false };
	}

	componentDidMount() {

	}


	render() { 
		return <main>
	  		<article className="authorization-card">
				<section className="authorization-type">
					<div className={`${this.state.showRegFields ? '' : 'active'} non-select login`} data-text="CONTINUE" onClick={() => this.setState({showRegFields: false})}>Login</div>
					<div className={`${this.state.showRegFields ? 'active' : ''} non-select signup`} data-text="CREATE ACCOUNT" onClick={() => this.setState({showRegFields: true})}>Sign up</div>
				</section>
				<section className="authorization-info sign-in">
					<div>
						<input id="username" type="text" placeholder="username" required/>
						<span>username</span>
					</div>

						{ this.state.showRegFields ? (
							<div className="signup-fields">
								<input id="email" placeholder="email" required/>
								<span>email</span>
							</div>
						) : ''
						}
						{ this.state.showRegFields ? (
							<div className="signup-fields">
								<input id="phone" placeholder="phone" required/>
								<span>phone</span>
							</div>
						) : ''
						}

					<div>
						<input id="password" type="password" placeholder="password" required/>
						<span>password</span>
					</div>
				</section>
				<section className="authorization-confirm">
					<button onClick={async (e) => {
						const $nameInput = $jsf(`#username`);
						const $passInput = $jsf(`#password`);
						const $emailInput = $jsf(`#email`);
						const $phoneInput = $jsf(`#phone`);

						console.log($nameInput)

						let user = {
							login: $nameInput.value(),
							password: $passInput.value(),
							email: $emailInput.get() && $emailInput.value() || null,
							phone: $phoneInput.get() && $phoneInput.value() || null
						}

						console.log(user)
						console.log(e.target.innerText())
						return; 
						let responseData;

						// if (e.target.innerText() == "CREATE ACCOUNT")
						// 	responseData = await DB.Registry(user);
						// else
						// 	responseData = await DB.Login(user);


						// if (responseData != null) {
						// 	LocalDB.SetToken(responseData['token']);

						// 	if (responseData['role'] == 'ROLE_USER')
						// 		window.location.replace('/mypolls/')
						// }
					}}>{this.state.showRegFields ? 'CREATE ACCOUNT' : 'CONTINUE'}</button>
				</section>
			</article>
  		</main>
	}
}
export default Authorize;
