import React from 'react';
import { DB } from '../polls/scripts/ApiDB';
import '../styles/list-poll.css';

class ListPoll extends React.Component {

	constructor(props) {
		super(props);

		this.state = ({
			userPolls: []
		});

		this.props.DB.GetUserPolls().then(result => { 
			this.setState({ userPolls: result })
		});
	}

	render() {

		return <section id="listPollView">
			{ 

				this.state.userPolls.map((row, i) => {
					return <div className="poll-elem" key={i++}>
						<h3>{row.name} 
							<span className="deleteIcon" onClick={() => {
								this.state.userPolls.splice(i-1, 1)
								DB.DeletePoll(`${row.link}`);
								this.setState({ userPolls: this.state.userPolls });
							}}></span>
						</h3>
						<span>Количество ответов: {row.replyAmount}</span>
						<span>Дата создания: {row.creationTime.replace(/T[\s\S]+/g, '')}</span>
						<span>Дата окончания: {row.expirationTime.replace(/T[\s\S]+/g, '')}</span>

						<div>
							<button onClick={() => { navigator.clipboard.writeText(`http://localhost:3000/reply/${row.link}`); alert('Скопировано')}}>Скопировать ссылку</button>
							<button onClick={() => { window.open(`http://localhost:3000/reply/${row.link}`, '_blank')}}>Пройти опрос</button>
							<button onClick={() => {
								this.props.ShowReplyList();
								// onClick={() => { window.open(`http://localhost:3000/replyList/${row.link}`, '_blank')}}
							}}>Посмотреть ответы</button>
						</div>
					</div> 
				})	

			}
		</section>
	}
}
export { ListPoll };
