import React from 'react';

import { useParams } from "react-router-dom";
import { Header } from '../components/Header';
import { DB } from './scripts/ApiDB';


const ReplyView = (props) => { 

	const { link } = useParams();

	return <ReplyComponent link={link}/>
}


class ReplyComponent extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			replies: []
		}

		DB.GetReply(this.props.link).then(result => {
			console.log(result)
			this.setState({ replies: result })
		});
	}

	render() {
		let questions = [];
    	let answers = [];
		if (this.state.replies && this.state.replies.questions) {
			questions = this.state.poll.questions.split('|');
    		answers = this.state.poll.answers.split('|');
		}
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						questions.split('|').map((question, i) => {
							return <article className="question-form" key={++i}>
								<section>
									<div>{question}</div>
								</section>

								<section>
									<div>Ответ: </div>
									<input />
								</section>
							</article>
						}) || <></>
					}
					<button id="postReplyBtn" onClick={() => {
						let answers = '';

						let $answers = document.getElementsByTagName('input');
						for (let i = 0; i < $answers.length; i++)
							answers += $answers[i].value + '|';

						answers = answers.replace(/(\|)$/g, '');

						let replyObj = {
							pollLink: this.props.link,
							answers: answers
						};

						DB.PostReply(replyObj).then(result => {
							window.location.replace(`http://localhost:3000/replyView/${result['link']}`)
						});
					}}>Завершить</button>
					</section>
				</article>
			</main>
		</>
	}
}
export { ReplyView };
