import React from 'react';

import { useParams } from "react-router-dom";
import { Header } from '../components/Header';
import { DB } from './scripts/ApiDB';


const Reply = (props) => { 

	const { link } = useParams();

	return <ReplyComponent link={link}/>
}


class ReplyComponent extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			poll: []
		}

		DB.GetPoll(this.props.link).then(result => { 
			console.log(result)
			this.setState({ poll: result })
		});
	}

	render() {
		let arr = '';
		if (this.state.poll && this.state.poll.questions)
			arr = this.state.poll.questions;

		console.log(arr)
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						arr.split('|').map((question, i) => {
							return <article className="question-form" key={++i}>
								<section>
									<div>${question}</div>
								</section>

								<section>
									<div>Ответ: </div>
									<input />
								</section>
							</article>
						})
						|| <></>
					}
					</section>
				</article>
			</main>
		</>
	}
}
export { Reply };
