if (!localStorage['token']) {
    localStorage['token'] = JSON.stringify({});
}


class LocalStorage {

    static GetToken() {
        return  localStorage['token'];
    }
    static SetToken(token) {
        localStorage['token'] = token;
    }

    static GetLogin() {
        return  localStorage['login'];
    }
    static SetLogin(token) {
        localStorage['token'] = token;
    }
    
}

export { LocalStorage }
