import React from 'react';
import './authorize/styless/fonts.css';
import './authorize/styless/layout.css';
import './authorize/styles/style.css';
import './authorize/styles/beauty_input.css';


export const Authorize = () => {

	const { showAuthorize, setShowAuthorize } = React.useState(false);

	function toggleMainView() {
        setShowAuthorize(true);
    }

	  return <main>
	  <article className="authorization-card">
		  <section className="authorization-type">
			  <div className="active non-select login" data-text="CONTINUE">Login</div>
			  <div className="non-select signup" data-text="CREATE ACCOUNT">Sign up</div>
		  </section>
		  <section className="authorization-info sign-in">
			  <div>
				  <input id="username" type="text" placeholder="username" required/>
				  <span>username</span>
			  </div>

				{ showAuthorize ? (
					<div>
					<div className="signup-fields">
					<input id="email" placeholder="email" required/>
					<span>email</span>
					</div>
					<div className="signup-fields">
						<input id="phone" placeholder="phone" required/>
						<span>phone</span>
					</div>
					</div>
				) : (<></>)
				}
			  <div>
				  <input id="password" type="password" placeholder="password" required/>
				  <span>password</span>
			  </div>
		  </section>
		  <section className="authorization-confirm">
			  <button>CONTINUE</button>
		  </section>
	  </article>
  </main>
}

export default Authorize;
