import React from 'react';
import '../styles/fonts.css';
import '../styles/layout.css';
import '../styles/header.css';
import '../styles/sidebar.css';

import '../styles/views.css';

import { Header } from '../components/Header';
import { DB } from './scripts/ApiDB';


class Reply extends React.Component {

	constructor(props) {
		super(props);

		this.state = {
			poll: []
		}

		this.props.DB.GetPoll().then(result => { 
			this.setState({ userPolls: result })
		});
	}

	render() {
		return <>
			<Header />
			<aside></aside>
			<main>
				<article id="views">
					<section id="replyPollView">
					{
						result.questions.split('|').map((question, i) => {
							return <article className="question-form" key={++i}>
								<section>
									<div>${question}</div>
								</section>

								<section>
									<div>Ответ: </div>
									<input />
								</section>
							</article>
						})
					}
					</section>
				</article>
			</main>
		</>
	}
}
export { Reply };
